ace.require("ace/ext/language_tools");const jsEditor=ace.edit("js-editor");function runJS(){let code=jsEditor.getValue();const outputDiv=document.getElementById("output");outputDiv.innerText="";const originalLog=console.log;console.log=function(e){outputDiv.innerText+=e+"\n"};try{eval(code)}catch(e){outputDiv.innerText=e}finally{console.log=originalLog}}jsEditor.setTheme("ace/theme/monokai"),jsEditor.session.setMode("ace/mode/javascript"),jsEditor.setOptions({enableBasicAutocompletion:!0,enableLiveAutocompletion:!0,fontSize:"14px",showPrintMargin:!1});